// Small compatibility wrapper: some templates request /js/bootstrap.bundle.min.js
// This file ensures bootstrap.min.js is loaded if present.
(function(){
  try {
    if (typeof bootstrap === 'undefined') {
      var s = document.createElement('script');
      s.src = '/js/bootstrap.min.js';
      s.async = false;
      document.head.appendChild(s);
    }
  } catch(e) {
    console && console.warn && console.warn('bootstrap.bundle wrapper failed', e);
  }
})();
